/**
 * @description determine if an array contains one or more items from another array.
 * @param {array} haystack the array to search.
 * @param {array} arr the array providing items to check for in the haystack.
 * @return {boolean} true|false if haystack contains at least one item from arr.
 * http://stackoverflow.com/a/25926600/922115
 */
TimesheetUtils.findOne = function(haystack, arr) {
    return arr.some(function (v) {
        return haystack.indexOf(v) >= 0;
    });
};

TimesheetUtils.replaceAll = function(string, toBeReplaced, replacement) {
    return string.split(toBeReplaced).join(replacement);
};

TimesheetUtils.uniqueArray = function(array) {
    var a = array.concat();
    for(var i = 0; i < a.length; i++) {
        for(var j = i + 1; j < a.length; j++) {
            if(a[i] === a[j]) {
                a.splice(j--, 1);
            }
        }
    }
    return a;
};

TimesheetUtils.getTags = function(value) {
    var result = [];
    if (value) {
        var items = (" " + value).match(/\s#\S*/g);
        if (items && items.length > 0) {
            angular.forEach(items, function (object) {
                result.push(object.substring(2));
            });
        }
    }
    return result;
};

