configurationModule.service("configurationService", ['$q', '$http', 'dataService', 'applicationLoggingService', function ($q, $http, dataService, logger) {
    var DATA_PROPERTY_CONFIGURATION = 'configuration';

    var AP = window.RA || window.AP;

    // DO data in services: http://blog.nebithi.com/angularjs-dos-and-donts/
    this.data = {
        allCompositionIssueLinks: null,
        allParentIssueFields: null
    };

    this.defaults = [
        {key: 'auditorsGroups'},
        {key: 'compositionIssueLink'},
        {key: 'parentIssueField'},
        {key: 'weekendType', val: '60'},
        {key: 'onErrorIgnore'},
        {key: 'prettyDuration'},
        {key: 'maxFractionDigits'},
        {key: 'trackSupportData'},
        {key: 'workLabels', val: []}
    ];

    var $config = null;
    this.getConfiguration = function() {
        if ($config == null) {
            $config = dataService.getProperty(DATA_PROPERTY_CONFIGURATION).then(function(data) {
                var configuration = {};
                for (var i = 0; i < this.defaults.length; i++) {
                    var config = this.defaults[i];
                    configuration[config.key] = config;
                }
                for (var i = 0; i < data.length; i++) {
                    var config = data[i];
                    if (config.key in configuration) {
                        configuration[config.key] = config;
                    }
                }
                return configuration;
            }.bind(this));
        }
        return $config;
    };

    this.saveConfiguration = function(config) {
        var hasValue = config.val && (!angular.isArray(config.val) || config.val.length > 0);
        if (hasValue) {
            if (config.id) {
                //update
                return this.updateConfiguration(config);
            } else {
                //create
                return this.addConfiguration(config);
            }
        } else {
            //delete
            return this.deleteConfiguration(config);
        }
    };

    this.addConfiguration = function(config) {
        var deferred = $q.defer();
        var configurationService = this;
        dataService.getProperty(DATA_PROPERTY_CONFIGURATION, []).then(function(configs) {
            var last = configs.length > 0 ? configs[configs.length - 1] : {id: 0};
            var newConfig = angular.copy(config);
            newConfig.id = last.id + 1;
            configs.push(newConfig);
            dataService.setProperty(DATA_PROPERTY_CONFIGURATION, configs).then(function() {
                deferred.resolve(newConfig);
            });
        });
        return deferred.promise;
    };

    this.updateConfiguration = function(config) {
        var deferred = $q.defer();
        var configurationService = this;
        dataService.getProperty(DATA_PROPERTY_CONFIGURATION, []).then(function(configs) {
            var i = configurationService.indexById(config.id, configs);
            var updatedConfig = angular.copy(config);
            configs[i] = updatedConfig;
            dataService.setProperty(DATA_PROPERTY_CONFIGURATION, configs).then(function() {
                deferred.resolve(updatedConfig);
            });
        });
        return deferred.promise;
    };

    this.deleteConfiguration = function(config) {
        var deferred = $q.defer();
        var configurationService = this;
        dataService.getProperty(DATA_PROPERTY_CONFIGURATION, []).then(function(configs) {
            var i = configurationService.indexById(config.id, configs);
            configs.splice(i, 1);
            dataService.setProperty(DATA_PROPERTY_CONFIGURATION, configs).then(function() {
                deferred.resolve({key: config.key});
            });
        });
        return deferred.promise;
    };

    this.getAllCompositionIssueLinks = function() {
        var service = this;
        var deferred = $q.defer();

        if (service.data.allCompositionIssueLinks == null) {
            AP.request({
                url: '/rest/api/2/issueLinkType',
                dataType: 'json',
                success: function(data) {
                    data = TimesheetUtils.getJson(data);
                    service.data.allCompositionIssueLinks = [];
                    for (var i = 0; i < data.issueLinkTypes.length; i++) {
                        service.data.allCompositionIssueLinks.push(data.issueLinkTypes[i].name);
                    }
                    deferred.resolve(service.data.allCompositionIssueLinks);
                },
                error: logger.error
            });
        } else {
            deferred.resolve(service.data.allCompositionIssueLinks);
        }
        return deferred.promise;
    };

    this.getAllParentIssueFields = function() {
        var service = this;
        var deferred = $q.defer();

        if (service.data.allParentIssueFields == null) {
            AP.request({
                url: '/rest/api/2/field',
                dataType: 'json',
                success: function(data) {
                    data = TimesheetUtils.getJson(data);
                    service.data.allParentIssueFields = [];
                    for (var i = 0; i < data.length; i++) {
                        if (data[i].custom == true && data[i].schema != null && data[i].schema.custom == "com.pyxis.greenhopper.jira:gh-epic-link") {
                            service.data.allParentIssueFields.push({id:data[i].id, name: data[i].name});
                        }
                    }
                    deferred.resolve(service.data.allParentIssueFields);
                },
                error: logger.error
            });
        } else {
            deferred.resolve(service.data.allParentIssueFields);
        }

        return deferred.promise;
    };

    this.resetState = function() {
        $config = null;
        this.data.allCompositionIssueLinks = null;
        this.data.allParentIssueFields = null;
    };

    this.indexById = function(id, array) {
        var index = -1;
        array.forEach(function(object, i) {
            if (id == object.id) {
                index = i;
                return;
            }
        });
        return index;
    };

}]);

configurationModule.service('dataService', ['$q', 'applicationLoggingService', '$http', function ($q, logger, $http) {
    var AP = window.RA || window.AP;
    var ADDON_URL = '/rest/atlassian-connect/1/addons/timereports';
    var PROPERTIES_URL = ADDON_URL + '/properties/';

    // https://developer.atlassian.com/static/connect/docs/latest/concepts/hosted-data-storage.html

    var $init = $q.defer();
    var hasProperty = {};

    this.getProperty = function(name, defaultValue) {
        var deferred = $q.defer();

        $init.promise.then(function() {
            if (!hasProperty[name]) {
                deferred.resolve(defaultValue || {});
                return;
            }

            AP.request({
                url: PROPERTIES_URL + name,
                success: function(data) {
                    var property = TimesheetUtils.getJson(data);
                    var value = TimesheetUtils.getJson(property.value);
                    deferred.resolve(value);
                },
                error: function(e) {
                    deferred.resolve(defaultValue || {});
                }
            });
        });
        return deferred.promise;
    };

    this.setProperty = function(name, value) {
        var deferred = $q.defer();

        AP.request({
            url: PROPERTIES_URL + name,
            type: 'PUT',
            data: JSON.stringify(value),
            contentType: 'application/json',
            success: function(data) {
                var property = TimesheetUtils.getJson(data);
                var value = TimesheetUtils.getJson(property.value);
                hasProperty[name] = true;
                deferred.resolve(value);
            },
            error: function(e) {
                deferred.reject(e);
            }
        });
        return deferred.promise;
    };

    AP.request({
        url: PROPERTIES_URL,
        success: function(data) {
            data = TimesheetUtils.getJson(data);
            for (var i = 0; i < data.keys.length; i++) {
                var key = data.keys[i];
                hasProperty[key.key] = true;
            }
            $init.resolve();
        }
    });

    // ACJIRA-877
    AP.request({
        url: ADDON_URL,
        success: function(data) {
            logger.license = TimesheetUtils.getJson(data);
        }
    });
}]);

