directives.directive('timespent', [function() {
  return {
    require: 'ngModel',
    link: function(scope, elm, attrs, ctrl) {

      ctrl.$validators.timespent = function(modelValue, viewValue) {
        if (!viewValue || /^\d*\.?\d+$/.test(viewValue)) {
            return !viewValue || parseFloat(viewValue) > 0;
        }
        var blocks = viewValue.split(" ");
        var units = ['w', 'd', 'h', 'm'];
        var result = blocks.length > 0;
        for (var i = 0; i < blocks.length && result && units.length > 0; i++) {
            do {
                var unit = units.shift();
                var r = RegExp('^(\\d*\\.?\\d+)' + unit + '$');
                var m = blocks[i].match(r);
                result = m != null && parseFloat(m[1]) > 0;
            } while (units.length > 0 && !result);
        }
        return result;
      };
    }
  };
}]);

