function GadgetSummary(selectedOption, currentUser, pivotTableType) {

    var getItemsByKey = function(key, labelSuffix) {
        var result = [];
        for (var i = 0; i < selectedOption.length; i++) {
            if (selectedOption[i].key == key && (labelSuffix == null || labelSuffix == /[^.]*$/.exec(selectedOption[i].label))) {
                result.push(selectedOption[i]);
            }
        }
        return result;
    };

    var getItemValue = function(items) {
        var result = "";
        for (var i = 0; i < items.length; i++) {
            if (result.length > 0) {
                result += ", ";
            }
            result += items[i].labelParam;
        }
        return result;
    };

    var createSummaryItem = function(text, styleClass) {
        return {
            text: text,
            styleClass: styleClass
        };
    };

    this.getSummaryItems = function() {
        var result = [];
        result.push(createSummaryItem("Summary ", "grey"));

        var allUsers = getItemValue(getItemsByKey("allUsers"));
        var value_group = getItemValue(getItemsByKey("group"));
        var value_project = getItemValue(getItemsByKey("filterOrProjectId", "project"));
        var value_filter = getItemValue(getItemsByKey("filterOrProjectId", "filter"));
        var value_startDate = getItemValue(getItemsByKey("startDate"));
        var value_endDate = getItemValue(getItemsByKey("endDate"));
        var value_groupedBy = getItemValue(getItemsByKey("groupByField"));

        if (allUsers && ['Timesheet', 'PieChart'].indexOf(pivotTableType) >= 0) {
            result.push(createSummaryItem("for all users", "grey"));
        } else if (value_group) {
            result.push(createSummaryItem("for groups", "grey"));
            result.push(createSummaryItem(value_group));
        } else if (currentUser) {
            result.push(createSummaryItem("for user", "grey"));
            result.push(createSummaryItem(currentUser.displayName || currentUser.fullName));
        }
        if (value_project != null && value_project.length != "") {
            if (result.length > 1) {
                result.push(createSummaryItem("and", "grey"));
            } else {
                result.push(createSummaryItem("for", "grey"));
            }
            result.push(createSummaryItem("project ", "grey"));
            result.push(createSummaryItem(value_project));
        } else if (!value_filter && ['Timesheet', 'PieChart'].indexOf(pivotTableType) < 0) {
            if (result.length > 1) {
                result.push(createSummaryItem("and", "grey"));
            } else {
                result.push(createSummaryItem("for", "grey"));
            }
            result.push(createSummaryItem('all projects'));
        }
        if (value_filter != null && value_filter.length != "") {
            if (result.length > 1) {
                result.push(createSummaryItem("and", "grey"));
            }
            result.push(createSummaryItem("filter", "grey"));
            result.push(createSummaryItem(value_filter));
        }
        if (value_groupedBy != null && value_groupedBy != "") {
            result.push(createSummaryItem("grouped by", "grey"));
            result.push(createSummaryItem(value_groupedBy));
        }
        if (value_startDate != null && value_startDate != "") {
            result.push(createSummaryItem("from", "grey"));
            result.push(createSummaryItem(value_startDate));
        }
        if (value_endDate != null && value_endDate != "") {
            result.push(createSummaryItem("to", "grey"));
            result.push(createSummaryItem(value_endDate));
        }
        return result;
    };
};
