define('app/modules/logging',['bower_components/stacktrace/stacktrace'], function(stacktrace) {


// http://engineering.talis.com/articles/client-side-error-logging/
var loggingModule = angular.module('talis.services.logging', []);

/**
 * Service that gives us a nice Angular-esque wrapper around the
 * stackTrace.js pintStackTrace() method. 
 */
loggingModule.factory(
    "traceService",
    function(){
        return({
            print: stacktrace // printStackTrace
        });
    }
);

/**
 * Override Angular's built in exception handler, and tell it to 
 * use our new exceptionLoggingService which is defined below
 */
loggingModule.provider(
    "$exceptionHandler",{
        $get: ['exceptionLoggingService', function(exceptionLoggingService){
            return(exceptionLoggingService);
        }]
    }
);

/**
 * Exception Logging Service, currently only used by the $exceptionHandler
 * it preserves the default behaviour ( logging to the console) but 
 * also posts the error server side after generating a stacktrace.
 */
loggingModule.factory(
    "exceptionLoggingService",
    ["$log","$window", "traceService", "applicationLoggingService",
    function($log, $window, traceService, logger) {
        function error(exception, cause){

            // preserve the default behaviour which will log the error
            // to the console, and allow the application to continue running.
            $log.error.apply($log, arguments);

            // now try to log the error to the server side.
            try {
                var errorMessage = exception.toString();

                // use our traceService to generate a stack trace
                var stackTrace = traceService.print({e: exception});

                // use AJAX (in this example jQuery) and NOT 
                // an angular service such as $http 
                if ($window.loggerUrl) $.ajax({
                    type: "POST",
                    url: $window.loggerUrl,
                    headers: {Authorization: "JWT " + $window.atlToken},
                    contentType: "application/json",
                    data: angular.toJson({
                        url: $window.location.href,
                        message: errorMessage,
                        type: "exception",
                        stackTrace: stackTrace,
                        tracelog: logger.flush(),
                        cause: decodeURIComponent(cause || $window.location.hash),
                        licene: {evaluation: logger.license.evaluation, maximumNumberOfUsers: logger.license.maximumNumberOfUsers},
                        deployedHash: $window.deployedHash
                    })
                });

                var $onErrorPopup = $window.$onErrorPopup;
                if ($onErrorPopup) {
                    $onErrorPopup.show(null, '.section-header');
                    $onErrorPopup.data('errorMessage', errorMessage).data('stackTrace', stackTrace);
                }
            } catch (loggingError){
                $log.warn("Error server-side logging failed");
                $log.log(loggingError);
            }
        }
        return(error);
    }]
);

/**
 * Application Logging Service to give us a way of logging 
 * error / debug statements from the client to the server.
 */
loggingModule.factory(
    "applicationLoggingService",
    ["$log","$window", function($log, $window) {
        return({
            license: {},
            messages: [],
            trace: function(message, args) {
                this.messages.push(message + ' ' + JSON.stringify(args));
            },
            flush: function() {
                return this.messages.splice(0, this.messages.length);
            },
            error: function(message, cause){
                // preserve default behaviour
                $log.error.apply($log, arguments);

                try {
                    // send server side
                    if ($window.loggerUrl) $.ajax({
                        type: "POST",
                        url: $window.loggerUrl,
                        headers: {Authorization: "JWT " + $window.atlToken},
                        contentType: "application/json",
                        data: angular.toJson({
                            url: $window.location.href,
                            message: message,
                            type: "error",
                            tracelog: this.flush(),
                            cause: decodeURIComponent(cause || $window.location.hash),
                            licene: {evaluation: this.license.evaluation, maximumNumberOfUsers: this.license.maximumNumberOfUsers},
                            deployedHash: $window.deployedHash
                        })
                    });
                } catch (loggingError){
                    $log.warn("Error server-side logging failed");
                    $log.log(loggingError);
                }
            },
            debug: function(message, args) {
                $log.log.apply($log, arguments);
                try {
                    // send server side
                    if ($window.loggerUrl) $.ajax({
                        type: "POST",
                        url: $window.loggerUrl,
                        headers: {Authorization: "JWT " + $window.atlToken},
                        contentType: "application/json",
                        data: angular.toJson({
                            message: message + ' ' + JSON.stringify(args),
                            licene: {evaluation: this.license.evaluation, maximumNumberOfUsers: this.license.maximumNumberOfUsers},
                            type: "debug"
                        })
                    });
                } catch (loggingError){
                    $log.warn("Error server-side logging failed");
                    $log.log(loggingError);
                }
            }
        });
    }]
);

});

