var DrawHelper = {
    composeTooltip: function (label, series, data, isGadget) {
        return '<table cellspacing="2px" cellpadding="2px">' +
            '<tr>' +
            this.prepareHtmlForIcons(data, label, "<br/>") +
            '<td>' +
            this.prepareLinkForItem(data, label, isGadget) +
            '<br/>' + Math.round(series.percent) + '%' +
            '</td>' +
            '</tr>' +
            '</table>'
    },
    composeLegendTooltip: function(label, series, data, formatConfig, isGadget) {
        var tooltipHint = this.getTooltipHint(this.getDataItemByLabel(data, label));
        return '<table>' +
            '<tr>' +
            this.prepareHtmlForIcons(data, label, "") +
            '<td>' + this.prepareLinkForItem(data, label) +
            (tooltipHint ? '&nbsp;' + this.truncate(tooltipHint, isGadget) : '') +
            '&nbsp;(' + this.getFormattedTime(series.data[0][1], formatConfig) + ')' +
            '</td>' +
            '</tr>' +
            '</table>'
    },
    prepareHtmlForIcons: function(data, label, separator) {
        var icons = this.getTooltipIcons(data, label);
        var html = "";
        if (icons != null && icons.length > 0) {
            var width = (separator == '') ? 16 * icons.length : 16;
            html += "<td style='width:"+width+"px; white-space: nowrap'>";
            for (var i = 0; i < icons.length; i++) {
                if (i != 0) {
                    html += separator;
                }
                html += '<img src="'+icons[i].url+'" title="'+ this.escapeQuotes(icons[i].title)+'" height="16"/>';
            }
            html += "</td>";
        }
        return html;
    },
    truncate: function(label, isGadget) {
        var maxLength = isGadget ? 20 : 50;
        return label.length > maxLength
            ? '<span title="' + this.escapeQuotes(label) + '">' + this.escapeHtml(label.substring(0, maxLength)) + '</span>'
            : this.escapeHtml(label);
    },
    prepareLinkForItem: function(data, label, isGadget) {
        var dataItem = this.getDataItemByLabel(data, label);
        var tooltipUrl = this.getTooltipUrl(dataItem);
        if (tooltipUrl) {
            var tooltipHint = this.getTooltipHint(dataItem);
            var resLabel = this.getTitleWithResolution(dataItem.link);
            return this.getMasterUrlHtml(dataItem) +
                this.getLinkHtml(tooltipUrl, tooltipHint, resLabel, '');
        } else {
            return this.truncate(label, isGadget);
        }
    },
    getTooltipIcons: function(data, label) {
        var item = this.getDataItemByLabel(data, label);
        if (item != null) {
            return item.icons;
        }
        return null;
    },
    getDataItemByLabel: function(data, label) {
        for (var i = 0; i < data.length; i++) {
            if (data[i].label == label) {
                return data[i];
            }
        }
        return null;
    },
    getTooltipUrl: function(dataItem) {
        if (dataItem != null && dataItem.link != null) {
            return dataItem.link.url;
        }
        return "#";
    },
    getTooltipHint: function(dataItem) {
        if (dataItem != null && dataItem.link && dataItem.link.hint != null) {
            return dataItem.link.hint;
        }
        return '';
    },
    getLinkHtml: function(url, hint, label, style) {
        return '<a title="'+this.escapeQuotes(hint)+'" href="'+url+ (style?'" class="'+style:'') +'">' + this.escapeHtml(label) + '</a>'
    },
    getMasterUrlHtml: function(dataItem) {
        if (dataItem != null) {
            var link = dataItem.masterLink;
            if (link != null) {
                var label = this.getTitleWithResolution(link);
                return this.getLinkHtml(link.url, link.hint, label, 'parentIssue2');
            }
        }
        return '';
    },
    getTitleWithResolution: function(linkObj) {
        return this.isResolvedIssue(linkObj)
            ? "<s>" + linkObj.label + "</s>"
            : linkObj.label;
    },
    getFormattedTime: function(seconds, formatConfig) {
        return formatConfig.timeFormat == 'pretty'
            ? this.prettyDuration(seconds, formatConfig)
            : this.formatDecimal(seconds / 3600, formatConfig.maxFractionDigits) + "h";
    },
    formatDecimal: function(decimal, maxFractionDigits) {
        return parseFloat(decimal.toFixed(maxFractionDigits || 2)).toLocaleString(); // Safari does not support options in toLocalString
    },
    isResolvedIssue: function(linkObj) {
        return linkObj != null && linkObj.resolution != null;
    },
    prettyDuration: function(seconds, formatConfig) {
        var value = parseInt(seconds) || 0;
        var secondsInMinute = 60;
        var secondsInHour = 60 * secondsInMinute;
        var secondsInDay = parseFloat(formatConfig.workingHoursPerDay) * secondsInHour;
        var secondsInWeek = parseFloat(formatConfig.workingDaysPerWeek) * secondsInDay;
        var secondsInMonth = 4 * secondsInWeek;
        var secondsInYear = 12 * secondsInMonth;
        var result = [];
        if (value >= secondsInYear) {
            var years = Math.floor(value / secondsInYear);
            result.push(years + "y");
            value -= years * secondsInYear;
        }
        if (value >= secondsInMonth) {
            var months = Math.floor(value / secondsInMonth);
            result.push(months + "m");
            value -= months * secondsInMonth;
        }
        if (value >= secondsInWeek) {
            var weeks = Math.floor(value / secondsInWeek);
            result.push(weeks + "w");
            value -= weeks * secondsInWeek;
        }
        if (value >= secondsInDay) {
            var days = Math.floor(value / secondsInDay);
            result.push(days + "d");
            value -= days * secondsInDay;
        }
        if (value >= secondsInHour) {
            var hours = Math.floor(value / secondsInHour);
            result.push(hours + "h");
            value -= hours * secondsInHour;
        }
        if (value >= secondsInMinute || result.length == 0) {
            var minutes = Math.floor(value / secondsInMinute);
            result.push(minutes + "m");
            value -= minutes * secondsInMinute;
        }
        return result.join(" ");
    },
    buildLabel: function(rowKey, row) {
        if (rowKey.indexOf(' 00:00:00 ') != -1) {
            rowKey = rowKey.replace(' 00:00:00 ', ''); // correct dates
        }
        if(row.rowKey.project) {
            var project = row.rowKey.project;
            if (project.name && project.key) {
                rowKey = rowKey.replace(project.name, project.key);
            }
        }
        return rowKey;
    },
    escapeQuotes: function(unsafe) {
        return unsafe.replace(/"/g, '\\"');
    },
    escapeHtml: function(unsafe) {
        return unsafe
             .replace(/&/g, "&amp;")
             .replace(/</g, "&lt;")
             .replace(/>/g, "&gt;")
             .replace(/"/g, "&quot;")
             .replace(/'/g, "&#039;");
    }
};
