app.directive('timePicker', ['$compile', function ($compile) {
    return {
        restrict: 'E',
        scope: {
            time: '='
        },
        link: function (scope, elem, attrs) {
            var isNumber = function(value) { // regexp because "1f" and "1d" is ok for parseInt();
                return /^\d+$/.test(value);
            };
            scope.timeClick = function(item, $event) {
                if (!isNumber(item.value)) {
                    scope.time.timePartsToMoment();
                    scope.time.momentTime.set('hour', item.step + scope.time.momentTime.get('hour'));
                    scope.time.momentToTimeParts();
                } else {
                    if ($event.shiftKey) {
                        item.value = parseInt(item.value) - item.step;
                        if (item.value < item.min) {
                            item.value = item.min;
                        }
                    } else {
                        item.value = parseInt(item.value) + item.step;
                        if (item.value > item.max) {
                            item.value = item.max;
                        }
                    }
                }
            };
            scope.timeChange = function(item) {
                if (item.min != null && item.max != null) {
                    if (!isNumber(item.value)) {
                        item.value = item.min;
                    } else if (item.min != null && item.max != null) {
                        if (item.value > item.max) {
                            item.value = item.max;
                        } else if (item.value < item.min) {
                            item.value = item.min;
                        }
                    }
                } else {
                    item.value = ""; // can't change it by typing, only by click
                }
            };
            $(elem).append($compile('<input type="text" ng-repeat-start="item in time.timeParts" maxLength="5" class="text" style="max-width: 40px; margin: 0 1px;" ng-model="item.value" ng-click="timeClick(item, $event)" ng-change="timeChange(item)">' +
                '<span ng-repeat-end ng-show="item.suffix">{{item.suffix}}</span>')(scope));
        }
    }
}]);

