function PivotRow(rowKey) {
    this.rowKey = rowKey;
    this.columns = {};
    this.sum = 0;
    this.data = [];
}
PivotRow.prototype.add = function(pivotEntry) {
    for (var i = 0; i < pivotEntry.columnKeys.length; i++) {
        var columnKey = pivotEntry.columnKeys[i];
        var value = pivotEntry.values[columnKey.keyValue];
        var column = PivotTable.getOrCreate(this.columns, columnKey, PivotColumn);
        column.add(pivotEntry, value);
    }
    if (typeof pivotEntry.value == 'number') {
        this.sum += pivotEntry.value;
    }
    this.data.push(pivotEntry);
    return column;
};
define("app/pivottable/PivotRow", function(){});

function PivotColumn(columnKey) {
    this.columnKey = columnKey;
    this.sum = 0;
    this.entries = [];
    this.value = null;
}
PivotColumn.prototype.add = function(pivotEntry, value) {
    this.entries.push(pivotEntry);
    if (typeof value == 'number') {
        this.sum += value;
    } else if (angular.isObject(value) && Object.keys(value).length > 0) {
        this.value = this.value || {};
        for (var key in value) {
            if (typeof value[key] == 'number') {
                this.value[key] = value[key] + (this.value[key] || 0);
            }
        }
    }
};
