TimesheetStrategy.prototype.getColumns = function(sourceFn) {
    
    return sourceFn(this.options, this.configOptions).map(function(c) {
        var column = new this.columnKeyClass(moment(c.date).toDate(), this.options);
        column.isToday = c.isToday;
        column.isWeekend = c.isWeekend;
        return column;
    }, this).reduce(function(result, column) {
        result[column.keyValue] = new PivotColumn(column);
        return result;
    }, {});
};
