function TimeTrackingStrategy(rowKeyClass, options) {
    this.rowKeyClass = rowKeyClass;
    this.options = {};
    this.configOptions = options.configOptions;
}
TimeTrackingStrategy.prototype.getEntries = function(issue) {
    var rowKey = new this.rowKeyClass(issue, null, this.configOptions);
    var plannedColumnKey = new PivotKey.Generic("PlannedVsActual", "1timeoriginalestimate");
    var remainColumnKey = new PivotKey.Generic("PlannedVsActual", "2esttimeremaining");
    var actualColumnKey = new PivotKey.Generic("PlannedVsActual", "3timespent");
    var diffColumnKey = new PivotKey.Generic("PlannedVsActual", "4diff");
    var progressColumnKey = new PivotKey.Generic("PlannedVsActual", "5progress", 'timeTracking');
    var original = issue.fields.timeoriginalestimate || 0;
    var entry = new PivotEntry(rowKey, plannedColumnKey, original);
    var actual = issue.fields.timespent || 0;
    var remaining = issue.fields.timeestimate || 0;
    entry.addColumn(remainColumnKey, remaining);
    entry.addColumn(actualColumnKey, actual);
    entry.addColumn(diffColumnKey, original - actual - remaining);
    entry.addColumn(progressColumnKey, {estimate: remaining, timespent: actual});
    return [entry];
};
