function PivotTable(pivotStrategy, logger) {
    this.pivotStrategy = pivotStrategy;
    this.rows = {};
    this._sortedRows = null;
    this._sortedColumns = null;
    this.totals = {};
    this.sum = 0;
    if (this.pivotStrategy.getColumnsFunction) {
        this.totals = this.pivotStrategy.getColumnsFunction();
    }
    this.logger = logger;
}

PivotTable.prototype.sortedColumns = function() {
    if (this._sortedColumns == null) {
        this._sortedColumns = [];
        var keys = Object.keys(this.totals);
        for (var i = 0; i < keys.length; i++) {
            this._sortedColumns.push(this.totals[keys[i]]);
        }
        if (this.pivotStrategy.columnKeyClass && this.pivotStrategy.columnKeyClass.hasOwnProperty("comparator")) {
            TimesheetUtils.sortByProperty(this._sortedColumns, "columnKey", false, this.pivotStrategy.columnKeyClass.comparator);
        }
    }
    return this._sortedColumns;
};

PivotTable.prototype.sortedRows = function() {
    if (this._sortedRows == null) {
        var performance = TimesheetUtils.getPerformance();
        var t1 = performance.now();
        var keys = Object.keys(this.rows);
        if (this.pivotStrategy.rowKeyClass.hasOwnProperty('comparator')) {
            keys.sort(this.pivotStrategy.rowKeyClass.comparator);
        }
        var t2 = performance.now();
        this._sortedRows = [];
        keys.map(function (key) {
            this._sortedRows.push(this.rows[key]);
        }, this);
        var t3 = performance.now();
        //keys.length > 100 && this.logger && this.logger.debug('sortedRows performance', {_: this.logger._  , rows: keys.length, time: t3 - t1, t1: t2 - t1, t2: t3 - t2});
        if (!this.pivotStrategy.options.showWeekends) {
            for (var key in this.totals) {
                if (this.totals[key].columnKey.isWeekend && !this.totals[key].sum) {
                    delete this.totals[key];
                    for (var i = 0; i < this._sortedRows.length; i++) {
                        delete this._sortedRows[i].columns[key];
                    }
                    for (var i = 0; i < this._sortedColumns.length; i++) {
                        if (this._sortedColumns[i].columnKey.keyValue == key) {
                            this._sortedColumns.splice(i, 1);
                        }
                    }
                }
            }
        }
    }
    return this._sortedRows;    
};

PivotTable.prototype.addEntry = function(pivotEntry) {
    var pivotRowType = this.pivotStrategy.pivotRowType || PivotRow;
    var pivotTable = this;
    var pivotRow = PivotTable.getOrCreate(this.rows, pivotEntry.rowKey, pivotRowType, function() {
        if (pivotTable.pivotStrategy.getColumnsFunction) {
            this.columns = pivotTable.pivotStrategy.getColumnsFunction();
        }
        this.moreFields = pivotTable.moreFields;
    });
    pivotRow.add(pivotEntry);
    for (var i = 0; i < pivotEntry.columnKeys.length; i++) {
        var columnKey = pivotEntry.columnKeys[i];
        var value = pivotEntry.values[columnKey.keyValue];
        var total = PivotTable.getOrCreate(this.totals, columnKey, PivotColumn, function() {
            this.isTotalColumn = true;
        });
        total.add(pivotEntry, value);
    }
    if (typeof pivotEntry.value == 'number') {
        this.sum += pivotEntry.value;
    }
    this._sortedRows = null; // reset
};

PivotTable.prototype.add = function(issue) {
    var pivotEntries = this.pivotStrategy.getEntries(issue);
    for (i in pivotEntries) {
        var pivotEntry = pivotEntries[i];
        this.addEntry(pivotEntry);
    }
    return pivotEntries;
};

var PivotTableType = {
    TimeEntry: 0,
    Timesheet: 1,
    IssueWorkedTimeByUser: 2,
    IssueWorkedTimeByStatus: 3,
    TimeTracking: 4,
    PieChart: 5,
    IssuePassedTimeByStatus: 6
};

var PivotTableFactory = {}; // very basic singleton
PivotTableFactory.createPivotTable = function(options, logger) {
    var pivotStrategy;
    var groupByField = options.groupByField;
    var createStrategy = function(strategyClass, groupByField, columnKeyClass, options) {
        if (groupByField) {
            if (groupByField == 'workeduser') {
                return new strategyClass(PivotKey.groupByWorkedUserKey, columnKeyClass, options);
            } else if (groupByField == 'workeddate') {
                return new strategyClass(PivotKey.groupByWorkedDateKey, columnKeyClass, options);
            } else if (groupByField == 'workcreated') {
                return new strategyClass(PivotKey.groupByWorkCreatedKey, columnKeyClass, options);
            } else if (groupByField == 'commentfirstword') {
                return new strategyClass(PivotKey.groupByCommentFirstwordKey, columnKeyClass, options);
            } else if (groupByField == 'worklabel') {
                return new strategyClass(PivotKey.groupByWorkLabelKey, columnKeyClass, options);
            } else {
                return new strategyClass(PivotKey.makeIssueFieldKey(groupByField, options.groupByFieldObject), columnKeyClass, options);
            }
        } else {
            return new strategyClass(PivotKey.Issue, columnKeyClass, options);
        }
    };
    switch (PivotTableType[options.pivotTableType]) {
        case PivotTableType.PieChart:
        case PivotTableType.IssueWorkedTimeByUser:
            pivotStrategy = createStrategy(WorkedTimePivotStrategy, groupByField, PivotKey.User, options);
            break;
        case PivotTableType.IssueWorkedTimeByStatus:
            pivotStrategy = createStrategy(WorkedTimePivotStrategy, groupByField, PivotKey.Status, options);
            break;
        case PivotTableType.IssuePassedTimeByStatus:
            pivotStrategy = createStrategy(PassedTimePivotStrategy, groupByField, PivotKey.Status, options);
            break;
        case PivotTableType.TimeTracking:
            if (groupByField) {
                pivotStrategy = new TimeTrackingStrategy(PivotKey.makeIssueFieldKey(groupByField, options.groupByFieldObject), options);
            } else {
                pivotStrategy = new TimeTrackingStrategy(PivotKey.Issue, options);
            }
            break;
        case PivotTableType.Timesheet:
            var monthView = options.monthView;
            var defaultKeyFunction = monthView ? PivotKey.WeekOfTheYear : PivotKey.DayOfTheWeek;
            var keyFunction = this.createPivotKey(options, defaultKeyFunction);
            pivotStrategy = createStrategy(TimesheetStrategy, groupByField, keyFunction, options);
            var defaultColumnsFunc = monthView ? pivotStrategy.getMonthColumns : pivotStrategy.getWeekColumns;
            pivotStrategy.getColumnsFunction = this.createGetColumnFunction(pivotStrategy, options, defaultColumnsFunc);
            break;
        case PivotTableType.TimeEntry:
            pivotStrategy = new TimeEntryStrategy(PivotKey.Issue, options);
            break;
        default:
            throw "Unknown PivotTableType "+ options.pivotTableType;
    }
    var pivotTable = new PivotTable(pivotStrategy, logger);
    pivotTable.moreFields = options.moreFields;
    return pivotTable;
};

PivotTable.getOrCreate = function(dictionary, key, recordClass, init) {
    var record = dictionary[key.keyValue];
    if (record == undefined) {
        record = new recordClass(key);
        if (init) init.apply(record);
        dictionary[key.keyValue] = record;
    }
    return record;
};

PivotTableFactory.createGetColumnFunction = function(pivotStrategy, options, defaultColumnsFunc) {
    var result = defaultColumnsFunc;
    if (options != null) {
        switch (options.sum) {
            case "month":
                result = pivotStrategy.getYearColumns;
                break;
            case "week":
                result = pivotStrategy.getMonthColumns;
                break;
            case "day":
                result = pivotStrategy.getWeekColumns;
                break;
        }
    }
    return result;
};

PivotTableFactory.createPivotKey = function(options, defaultKeyFunc) {
    var result = defaultKeyFunc;
    if (options != null) {
        switch (options.sum) {
            case "month":
                result = PivotKey.MonthOfTheYear;
                break;
            case "week":
                result = PivotKey.WeekOfTheYear;
                break;
            case "day":
                result = PivotKey.DayOfTheWeek;
                break;
        }
    }
    return result;
};

