// see also PivotKey.makeIssueFieldKey
ExcelView.prototype.fieldValue = function(issue, fieldObject, options) {
    var NULL_VALUE = "";
    var sprintRegex = /com\.atlassian\.greenhopper\.service\.sprint\.Sprint@[\d\w]+\[(.+)?\]/;
    var maxFractionDigits = options.maxFractionDigits
    var hostBaseUrl = options.hostBaseUrl || '';

    var getNameOrKey = function(field) {
        var m;
        if (angular.isObject(field) && field.hasOwnProperty('name')) {
            return field.name;
        } else if (angular.isObject(field) && field.hasOwnProperty('key')) {
            return "<a href='" + hostBaseUrl + "/browse/" + field.key + "'>" + field.key + "</a>";
        } else if (angular.isObject(field) && field.hasOwnProperty('value')) {
            return field.value + (field.hasOwnProperty('child') ? ':' + getNameOrKey(field.child) : '');
        } else if (angular.isString(field) && (m = field.match(sprintRegex))) {
            var splits = m[1].split(',');
            for (var i = 0; i < splits.length; i++) {
                var split = splits[i];
                if (split.startsWith('name=')) {
                    return split.substr(5);
                }
            }
            return NULL_VALUE;
        } else {
            return field;
        }
    };

    var getValue = function(field) {
        if (TimesheetUtils.isDate(field)) {
            return moment(field).format(ExcelView.dateTimeFormat);
        } else if (Array.isArray(field)) {
            var value = '';
            field.forEach(function(v) {
                if (value.length > 0) {
                    value += ', ';
                }
                value += getNameOrKey(v);
            });
            return value;
        } else {
            return getNameOrKey(field);
        }
    };

    var fieldName = fieldObject.id || fieldObject;

    var getFieldValueObject = function(issue) {
        var value = issue.fields[fieldName];
        if (fieldObject && fieldObject.schema && fieldObject.schema.custom == 'com.pyxis.greenhopper.jira:gh-epic-link') {
            if (value) {
                value = {key: value};
            } else if (issue.fields.issuetype.name == 'Epic') {
                value = {key: issue.key};
            }
        }
        if (fieldName == 'project') {
            value = NULL_VALUE;
        }
        if (!value) {
            if (fieldName == 'parent') {
                value = {key : issue.key};
            } else {
                value = NULL_VALUE;
            }
        }
        return value;
    };

    this.field = getFieldValueObject(issue);
    var value = getValue(this.field);
    if (['timeestimate', 'timeoriginalestimate', 'timespent'].indexOf(fieldName) != -1 && value != NULL_VALUE) {
        value = DrawHelper.formatDecimal((value || 0) / 3600, maxFractionDigits) + 'h';
    }
    return value;
};
